import React, { useState } from 'react';
import { makeStyles} from '@mui/styles';
import { Avatar, Button, Dialog, DialogTitle, DialogContent, DialogActions, Theme } from '@mui/material';

type Friend = {
  id: number;
  name: string;
  avatar: string;
};

const friends: Friend[] = [
  { id: 1, name: 'Друг 1', avatar: '/path/to/avatar1.png' },
  { id: 2, name: 'Друг 2', avatar: '/path/to/avatar2.png' },
  // Добавьте остальных друзей с их аватарками
];

const useStyles = makeStyles((theme: Theme) => ({
  chatPanel: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
    display: 'flex',
    gap: theme.spacing(1),
  },
  avatar: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    cursor: 'pointer',
  },
  chatModal: {
    // Стили для модального окна чата
  },
}));

const Chat: React.FC = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [selectedFriend, setSelectedFriend] = useState<Friend | null>(null);

  const handleAvatarClick = (friend: Friend) => {
    setSelectedFriend(friend);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedFriend(null);
  };

  return (
    <>
      <aside className={classes.chatPanel}>
        {friends.map((friend) => (
          <Avatar
            key={friend.id}
            alt={friend.name}
            src={friend.avatar}
            className={classes.avatar}
            onClick={() => handleAvatarClick(friend)}
          />
        ))}
      </aside>

      <Dialog open={open} onClose={handleClose} className={classes.chatModal}>
        <DialogTitle>Чат с {selectedFriend?.name}</DialogTitle>
        <DialogContent>
          {/* Компоненты для отображения переписки */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Закрыть</Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default Chat;
