import React, { useState } from 'react';
import { makeStyles } from '@mui/styles';
import { Avatar, Drawer, Theme } from '@mui/material';

type Friend = {
  id: number;
  name: string;
  avatar: string;
};

const friends: Friend[] = [
  { id: 1, name: 'Друг 1', avatar: '/path/to/avatar1.png' },
  { id: 2, name: 'Друг 2', avatar: '/path/to/avatar2.png' },
  // Добавьте остальных друзей с их аватарками
];

const drawerWidth = 320;

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    chatPanel: {
      position: 'fixed',
      bottom: theme.spacing(2),
      right: theme.spacing(2),
      display: 'flex',
      gap: theme.spacing(1),
    },
    avatar: {
      width: theme.spacing(4),
      height: theme.spacing(4),
      cursor: 'pointer',
    },
    drawer: {
      width: drawerWidth,
    },
    chatContainer: {
      padding: theme.spacing(2),
    },
  })
);

const PopupChat: React.FC = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [selectedFriend, setSelectedFriend] = useState<Friend | null>(null);

  const handleAvatarClick = (friend: Friend) => {
    setSelectedFriend(friend);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedFriend(null);
  };

  return (
    <>
      <aside className={classes.chatPanel}>
        {friends.map((friend) => (
          <Avatar
            key={friend.id}
            alt={friend.name}
            src={friend.avatar}
            className={classes.avatar}
            onClick={() => handleAvatarClick(friend)}
          />
        ))}
      </aside>

      <Drawer
        anchor="right"
        open={open}
        onClose={handleClose}
        classes={{
          paper: classes.drawer,
        }}
      >
        {selectedFriend && (
          <div className={classes.chatContainer}>
            <div>
              Чат с {selectedFriend.name}
              <button onClick={handleClose}>Закрыть</button>
            </div>
            <div className="chat-messages">
              {/* Здесь может быть компонент переписки */}
            </div>
            <div className="chat-footer">
              <textarea placeholder="Введите сообщение..."></textarea>
              <button>Отправить</button>
            </div>
          </div>
        )}
      </Drawer>
    </>
  );
};

export default PopupChat;
